    {
        p_at_m161(1) = prm_928;
        p_at_m161 = 100;
    }
    if (original_level + p_at_m161(1) > 2000)
    {
        p_at_m161(1) = 2000 - original_level;
    }
    sdata.get(prm_926, prm_927).original_level +=
        std::clamp(p_at_m161(1), 0, 2000);
