void place_character()
{
    int placefail = 0;
    if (rc == -1)
    {
        return;
    }
    if (rc == 56)
    {
        cdata[rc].state = 0;
        return;
    }
    placefail = 0;
    if (gdata_mount == rc)
    {
        if (rc != 0)
        {
            cdata[rc].position.x = cdata[0].position.x;
            cdata[rc].position.y = cdata[0].position.y;
            return;
        }
    }
    {
        int cnt = 0;
        for (;; ++cnt)
        {
            if (cnt == 99)
            {
                if (rc >= 57)
                {
                    placefail = 1;
                    break;
                }
            }
            if (cnt > 99)
            {
                if (mdata(0) == 0)
                {
                    placefail = 1;
                    break;
                }
                y = (cnt - 100) / mdata(0);
                x = (cnt - 100) % mdata(0);
                if (y >= mdata(1))
                {
                    if (rc != 0)
                    {
                        placefail = 1;
                        break;
                    }
                    else
                    {
                        x = rnd(mdata(0));
                        y = rnd(mdata(1));
                        if (map(x, y, 1) != 0)
                        {
                            map(x, y, 1) = 0;
                        }
                    }
                }
            }
            else
            {
                x = rnd(mdata(0) - 4) + 2;
                y = rnd(mdata(1) - 4) + 2;
                if (cxinit >= 0)
                {
                    if (cnt == 0)
                    {
                        x = cxinit;
                        y = cyinit;
                    }
                    else
                    {
                        x = cxinit + rnd((cnt + 1)) - rnd((cnt + 1));
                        y = cyinit + rnd((cnt + 1)) - rnd((cnt + 1));
                    }
                }
                if (cnt < 20)
                {
                    if (cxinit == -2)
                    {
                        p = cdata[0].vision_distance / 2;
                        if (x >= cdata[0].position.x - p
                            && x <= cdata[0].position.x + p)
                        {
                            if (y >= cdata[0].position.y - p
                                && y <= cdata[0].position.y + p)
                            {
                                continue;
                            }
                        }
                    }
                }
            }
            if (x < 0 || y < 0 || x >= mdata(0) || y >= mdata(1))
            {
                continue;
            }
            if (chipm(7, map(x, y, 0)) & 4)
            {
                continue;
            }
            if (map(x, y, 1) != 0)
            {
                continue;
            }
            if (map(x, y, 6) != 0)
            {
                if (chipm(7, map(x, y, 6) % 1000) & 4)
                {
                    continue;
                }
                cell_featread(x, y);
                if (feat(1) == 11)
                {
                    if (rc != 0)
                    {
                        if (cxinit < 0)
                        {
                            continue;
                        }
                    }
                }
                if (feat(1) == 10)
                {
                    if (rc != 0)
                    {
                        if (cxinit < 0)
                        {
                            continue;
                        }
                    }
                }
            }
            cdata[rc].initial_position.x = x;
            cdata[rc].initial_position.y = y;
            map(x, y, 1) = rc + 1;
            cdata[rc].position.x = x;
            cdata[rc].position.y = y;
            p = 1;
            break;
        }
    }
    if (placefail == 1)
    {
        if (rc < 16)
        {
            cdata[rc].state = 8;
            txt(lang(
                name(rc) + u8"とはぐれた。"s,
                name(rc) + u8" loses "s + his(rc) + u8" way."s));
        }
        else
        {
            txt(lang(
                name(rc) + u8"は何かに潰されて息絶えた。"s,
                name(rc) + u8" is killed."s));
            cdata[rc].state = 0;
        }
        if (cdata[rc].character_role != 0)
        {
            cdata[rc].state = 2;
        }
        if (cdata[rc].character_role == 13)
        {
            cdata[rc].state = 4;
        }
    }
    return;
}



