int key_direction()
{
    kdx = 0;
    kdy = 0;
    if (key == key_north)
    {
        --kdy;
        return 1;
    }
    if (key == key_south)
    {
        ++kdy;
        return 1;
    }
    if (key == key_west)
    {
        --kdx;
        return 1;
    }
    if (key == key_east)
    {
        ++kdx;
        return 1;
    }
    if (key == key_northwest)
    {
        --kdx;
        --kdy;
        return 1;
    }
    if (key == key_northeast)
    {
        ++kdx;
        --kdy;
        return 1;
    }
    if (key == key_southwest)
    {
        --kdx;
        ++kdy;
        return 1;
    }
    if (key == key_southeast)
    {
        ++kdx;
        ++kdy;
        return 1;
    }
    return 0;
}



